import { Component, OnInit, ViewChild } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Category } from 'src/app/Model/category.model';
import { CategoryService } from 'src/app/Model/Services/category/category.service';

@Component({
  selector: 'app-add-item',
  templateUrl: './add-item.component.html',
  styleUrls: ['./add-item.component.css']
})
export class AddItemComponent implements OnInit{

  @ViewChild('itemForm') form = NgForm;

  allCategories: any[] = [];

  constructor(private categoryService: CategoryService) { }

  ngOnInit() {
    this.fetchAllCategories();
    console.log(this.allCategories);

  }

  fetchAllCategories() {
    this.categoryService.getCategories().subscribe((data) => {
      this.allCategories.push(...data)
    })
  };

  onItemAdd(form:any) {

  }
}
<app-navbar></app-navbar>
<div class="container mt-2 mx-3">
    <h1>Add Item</h1>
    <form (ngSubmit)="onItemAdd(itemForm)" #itemForm="ngForm">
        <div class="form-group row col-md-6">
            <div class="col-xs-3">
                <label for="name">Name</label>
                <input type="text" class="form-control" name="name" id="name" placeholder="Enter Item Name">
            </div>
            <div class="col-xs-3 mt-2 dropup">
                <label for="category">Category</label>
                <select  name="category" class="form-control" required [class.is-invalid]="category.touched && category.invalid" #category = ngModel ngModel  >                
                    <option value="" selected>Select Category</option>
                    <option  *ngFor="let category of allCategories" [value]="category.category_name">{{category.name}}</option>              
                  </select>
            </div>
            <div class="col-xs-3 mt-2">
                <label for="description">Description</label>
                <textarea type="text" class="form-control" name="description" id="description" placeholder="Enter Item Description"></textarea>
            </div>

            <div class="col-xs-3 mt-2">
                <label for="price">Price</label>
                <input type="text" class="form-control" name="price" id="price" placeholder="Enter Item Price">
            </div>
            <div class="col-xs-3 mt-2">
                <label for="ratings">Ratings</label>
                <select onmousedown="if(this.options.length>3){this.size=3;}" onchange="this.blur()"  onblur="this.size=0;" class="form-select form-select-sm" name="ratings" id="ratings" aria-label=".form-select-sm example">
                    <option value="1">1</option>
                    <option value="2">2</option>
                    <option value="3">3</option>
                    <option value="4">4</option>
                    <option value="5">5</option>
                  </select>
            </div>
            <div class="col-xs-3 mt-2">
                <label for="discount">Discount</label>
                <input type="text" class="form-control" name="discount" id="discount" placeholder="Enter Item Discount">
            </div>

        </div>
        <input type="submit" class="btn buttons my-2" value= "Submit"  data-bs-toggle="modal" data-bs-target="#exampleModal">
    </form>
</div>
